import React, { Component } from "react";
import HTMLFlipBook from "react-pageflip";
import Profile from "pages/profile/Profile";
import Career from "pages/career/Career";
import Contact from "pages/contact/Contact";
import PortfolioPage from "components/PortfolioPage/PortfolioPage";

class Portfolio extends Component {
  constructor(props) {
    super(props);

    this.state = {
      portfolioArr: [
        {
          idx: 1,
          filterNum: "a",
          title: "test1",
          contents: "test1번의 포트폴리오",
        },
        {
          idx: 2,
          filterNum: "b",
          title: "test2",
          contents: "test2번의 포트폴리오",
        },
        {
          idx: 3,
          filterNum: "a",
          title: "test3",
          contents: "test3번의 포트폴리오",
        },
        {
          idx: 4,
          filterNum: "c",
          title: "test4",
          contents: "test4번의 포트폴리오",
        },
        {
          idx: 5,
          filterNum: "d",
          title: "test5",
          contents: "test5번의 포트폴리오",
        },
        {
          idx: 6,
          filterNum: "d",
          title: "test6",
          contents: "test6번의 포트폴리오",
        },
      ], //포트폴리오 정보 저장하는 state
    };
  }

  handleOnClick = (num) => {
    if (num === 0) {
      this.setState({});
    } else if (num === 1) {
      this.setState({});
    } else if (num === 2) {
      this.setState({});
    } else if (num === 3) {
      this.setState({});
    }
  };

  render() {
    const { portfolioArr } = this.state;

    let showPortfolioArr = (portfolioArr || []).map((portfolio, idx) => {
      return (
        <div className="test-rect">
          {portfolio.title} / {idx}
        </div>
      );
    });

    return (
      <div className="portfolio-container">
        <div className="portfolio-contents-container">포트폴리오 내용 부분</div>
        <div className="portfolio-filter-container">
          <div className="portfolio-filter">
            <div className="test-margin">aaaaaaa</div>
            <div className="test-margin">bbbbbbb</div>
            <div className="test-margin">ccccccc</div>
            <div className="test-margin">ddddddd</div>
          </div>
          <div className="portfolio-list">{showPortfolioArr}</div>
        </div>
        {/* <HTMLFlipBook width={100} height={100}>
          <div className="demoPage">
            <Contact />
          </div>
          <div className="demoPage">
            <PortfolioPage />
          </div>
          <div className="demoPage">
            <Contact />
          </div>
          <div className="demoPage">
            <PortfolioPage />
          </div>
        </HTMLFlipBook> */}
      </div>
    );
  }
}

export default Portfolio;
