import React, { Component } from "react";
import SkillProgress from "components/SkillProgress/SkillProgress";

class Career extends Component {
  constructor(props) {
    super(props);

    this.state = {
      licenseArr: [
        {
          acquisitionYear: "2014",
          acquireLicense: [
            {
              licenseName: "웹디자인기능사",
              issuingAgency: "한국산업인력공단",
              issueDate: "2014.04",
            },
            {
              licenseName: "SOKI 국제 디자인 & 일러스트레이션 입상",
              issuingAgency: "한국일러스트학회",
              issueDate: "2014.01",
            },
          ],
        },
        {
          acquisitionYear: "2009",
          acquireLicense: [
            {
              licenseName: "GTQ 포토샵",
              issuingAgency: "한국생산성본부 KPC",
              issueDate: "2009.10",
            },
            {
              licenseName: "정보기기운용기능사",
              issuingAgency: "한국산업인력공단",
              issueDate: "2009.07",
            },
          ],
        },
        {
          acquisitionYear: "2008",
          acquireLicense: [
            {
              licenseName: "정보기술자격 ITQ 한글파워포인트",
              issuingAgency: "한국생산성본부 KPC",
              issueDate: "2008.06",
            },
          ],
        },
        {
          acquisitionYear: "2007",
          acquireLicense: [
            {
              licenseName: "워드프로세서",
              issuingAgency: "대한상공회의소",
              issueDate: "2007.08",
            },
            {
              licenseName: "정보기술자격 ITQ 아래한글",
              issuingAgency: "한국생산성본부 KPC",
              issueDate: "2007.07",
            },
          ],
        },
      ], //자격증 정보 json
      skillArr: [
        {
          icon: "icon-adobephotoshop",
          percentVal: 80,
          color: "#00c8ff",
          contents:
            "Photoshop - 합성, 사진 편집 등의 2D 그래픽 작업을 비롯한 다양한 웹 시안 디자인 작업",
        },
        {
          icon: "icon-adobeillustrator",
          percentVal: 50,
          color: "#ff7c00",
          contents:
            "Illustrator - 디자인 목업, 배너, 플래카드 등과 아이콘, 로고, 심볼 등의 그래픽 디자인 작업",
        },
        {
          icon: "icon-adobeindesign",
          percentVal: 80,
          color: "#fd3f93",
          contents:
            "Indesign - 리플렛, 브로슈어, 카다로그, 책자 등의 인쇄물 편집 디자인 작업",
        },
        {
          icon: "icon-adobeaftereffects",
          percentVal: 60,
          color: "#d291ff",
          contents:
            "After Effects - 영상 보정, 자막, 모션그래픽 등의 편집과 음악, 효과음 합성 작업",
        },
        {
          icon: "icon-adobepremiere",
          percentVal: 80,
          color: "#ea77ff",
          contents:
            "Premier - 영상 자르고 붙이는 등의 컷 편집과 음악, 효과음 합성 작업",
        },
        {
          icon: "icon-embed2",
          percentVal: 60,
          color: "#000000",
          contents:
            "HTML+CSS - HTML로 구조화하고 CSS로 디자인을 적용하는 웹 디자인 및 퍼블리싱 작업",
        },
      ], //skill 정보 json
    };
  }

  render() {
    const { licenseArr, skillArr } = this.state;
    const { isPageFocus } = this.props;

    // let showLicense = licenseArr.map((license) => {
    //   return (
    //     <div className="license-contents">
    //       <span className="desc">
    //         {license.licenseName} -
    //         <span className="license-date">
    //           {license.issuingAgency}({license.issueDate})
    //         </span>
    //       </span>
    //     </div>
    //   );
    // });

    let showLicense = licenseArr.map((license) => {
      let acquireLicense = license.acquireLicense.map((elem) => {
        return (
          <div>
            <span>
              · <span className="desc">{elem.licenseName}</span>(
              {elem.issueDate}. {elem.issuingAgency})
            </span>
          </div>
        );
      });
      return (
        <li>
          <div className="timeline-content">
            <h1>2014</h1>
            {acquireLicense}
          </div>
        </li>
      );
    });

    let showSkill = skillArr.map((skill) => {
      return (
        <div className="chart">
          <i className={skill.icon}></i>
          <SkillProgress
            isPageFocus={isPageFocus}
            percentVal={skill.percentVal}
            color={skill.color}
            contents={skill.contents}
          />
        </div>
      );
    });

    return (
      <div className="career-container">
        <div className="license-skill-container">
          <div className="license-container">
            <div className="circle_top" />
            <div className="circle_bottom" />
            <div className="license-title">License</div>
            <div className="container">
              <div className="timeline">
                <ul>{showLicense}</ul>
              </div>
            </div>
            {/* <div className="container">
              <div className="timeline">
                <ul>
                  <li>
                    <div className="timeline-content">
                      <h1>2014</h1>
                      <div>
                        <span>
                          · <span className="desc">웹디자인기능사</span>
                          (2014/04. 한국산업인력공단)
                        </span>
                      </div>
                      <div>
                        <span>
                          ·{" "}
                          <span className="desc">
                            SOKI 국제 디자인&amp;일러스트레이션 입상
                          </span>
                          <br />
                          (2014/01. 한국일러스트학회)
                        </span>
                      </div>
                    </div>
                  </li>
                  <li>
                    <div className="timeline-content">
                      <h1>2009</h1>
                      <div>
                        <span>
                          · <span className="desc">GTQ 포토샵</span>(2009/10.
                          한국생산성본부 KPC)
                        </span>
                      </div>
                      <div>
                        <span>
                          · <span className="desc">정보기기운용기능사</span>
                          (2009/07. 한국산업인력공단)
                        </span>
                      </div>
                    </div>
                  </li>
                  <li>
                    <div className="timeline-content">
                      <h1>2008</h1>
                      <div>
                        <span>
                          ·{" "}
                          <span className="desc">
                            정보기술자격 ITQ 한글파워포인트
                          </span>
                          <br />
                          (2008/06. 한국생산성본부 KPC)
                        </span>
                      </div>
                    </div>
                  </li>
                  <li>
                    <div className="timeline-content">
                      <h1>2007</h1>
                      <div>
                        <span>
                          · <span className="desc text">워드프로세서</span>
                          (2007/08. 대한상공회의소)
                        </span>
                      </div>
                      <div>
                        <span>
                          ·{" "}
                          <span className="desc">
                            정보기술자격 ITQ 아래한글
                          </span>
                          <br />
                          (2007/07. 한국생산성본부 KPC)
                        </span>
                      </div>
                    </div>
                  </li>
                </ul>
              </div>
            </div> */}
          </div>
          <div className="skill-container">{showSkill}</div>
        </div>
      </div>
    );
  }
}

export default Career;
