import React, { Component } from "react";
import HTMLFlipBook from "react-pageflip";
import Profile from "pages/profile/Profile";
import Career from "pages/career/Career";
import Contact from "pages/contact/Contact";
import PortfolioPage from "components/PortfolioPage/PortfolioPage";

class Portfolio extends Component {
  constructor(props) {
    super(props);

    this.state = {
      portfolioArr: [
        {
          idx: 1,
          filterVal: "a",
          title: "test1",
          contents: "test1번의 포트폴리오",
        },
        {
          idx: 2,
          filterVal: "b",
          title: "test2",
          contents: "test2번의 포트폴리오",
        },
        {
          idx: 3,
          filterVal: "a",
          title: "test3",
          contents: "test3번의 포트폴리오",
        },
        {
          idx: 4,
          filterVal: "c",
          title: "test4",
          contents: "test4번의 포트폴리오",
        },
        {
          idx: 5,
          filterVal: "c",
          title: "test5",
          contents: "test5번의 포트폴리오",
        },
        {
          idx: 6,
          filterVal: "c",
          title: "test6",
          contents: "test6번의 포트폴리오",
        },
      ], //포트폴리오 정보 저장하는 state
    };
  }

  handleOnClick = (num) => {
    const { portfolioArr } = this.state;
    let _portfolioArr = [];

    if (num === 0) {
      _portfolioArr = portfolioArr;
    } else if (num === 1) {
      _portfolioArr = portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "a"
      );
    } else if (num === 2) {
      _portfolioArr = portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "b"
      );
    } else if (num === 3) {
      _portfolioArr = portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "c"
      );
    }

    console.log("portfolioArr ==== ", _portfolioArr);

    this.setState({ portfolioArr: _portfolioArr });
  };

  render() {
    const { portfolioArr } = this.state;

    let showPortfolioArr = (portfolioArr || []).map((portfolio, idx) => {
      return (
        <div className="test-rect">
          {portfolio.title} / {idx}
        </div>
      );
    });

    return (
      <div className="portfolio-container">
        <div className="portfolio-contents-container">포트폴리오 내용 부분</div>
        <div className="portfolio-filter-container">
          <div className="portfolio-filter">
            <div
              className="test-margin"
              onClick={() => {
                this.handleOnClick(0);
              }}
            >
              aaaaaaa
            </div>
            <div
              className="test-margin"
              onClick={() => {
                this.handleOnClick(1);
              }}
            >
              bbbbbbb
            </div>
            <div
              className="test-margin"
              onClick={() => {
                this.handleOnClick(2);
              }}
            >
              ccccccc
            </div>
            <div
              className="test-margin"
              onClick={() => {
                this.handleOnClick(3);
              }}
            >
              ddddddd
            </div>
          </div>
          <div className="portfolio-list">{showPortfolioArr}</div>
        </div>
        {/* <HTMLFlipBook width={100} height={100}>
          <div className="demoPage">
            <Contact />
          </div>
          <div className="demoPage">
            <PortfolioPage />
          </div>
          <div className="demoPage">
            <Contact />
          </div>
          <div className="demoPage">
            <PortfolioPage />
          </div>
        </HTMLFlipBook> */}
      </div>
    );
  }
}

export default Portfolio;
