import React, { Component } from "react";
import ReactFullpage from "@fullpage/react-fullpage";

class Menu extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  handleClickLogo = () => {};

  render() {
    const { showPageName } = this.props;

    return (
      <div className="menu-container">
        <ReactFullpage
          render={({ state, fullpageApi }) => {
            return (
              <div className="menu">
                <div
                  className={
                    showPageName === "Intro" ? "selected-menu" : "mouse-over"
                  }
                  onClick={() => fullpageApi.moveTo(1, 0)}
                >
                  Intro
                </div>
                <div
                  className={
                    showPageName === "Profile" ? "selected-menu" : "mouse-over"
                  }
                  onClick={() => fullpageApi.moveTo(2, 0)}
                >
                  Profile
                </div>
                <div
                  className={
                    showPageName === "Career" ? "selected-menu" : "mouse-over"
                  }
                  onClick={() => fullpageApi.moveTo(3, 0)}
                >
                  Career
                </div>
                <div
                  className={
                    showPageName === "Portfolio" ? "selected-menu" : "mouse-over"
                  }
                  onClick={() => fullpageApi.moveTo(4, 0)}
                >
                  Portfolio
                </div>
                <div
                  className={
                    showPageName === "Contact" ? "selected-menu" : "mouse-over"
                  }
                  onClick={() => fullpageApi.moveTo(5, 0)}
                >
                  Contact
                </div>
              </div>
            );
          }}
        />
      </div>
    );
  }
}

export default Menu;
