import React, { Component } from "react";
import profileImg from "../../images/profile.jpg";

class Profile extends Component {
  render() {
    let personalityArr = ["성실함", "적응을 잘하는", "꼼꼼함", "끈기있는"];
    let personalityComp = personalityArr.map((personality) => {
      return <span># {personality}</span>;
    });

    let educationArr = [
      { date: "2008.03 - 2011.02", content: "한국게임과학고등학교 졸업" },
      {
        date: "2011.03 - 2017.02",
        content: "인덕대학교 컴퓨터소프트웨어과 졸업",
      },
    ];
    let experiencesArr = [
      {
        date: "2020.08.14",
        content: "제 8회 청소년 게임캠프 2010 전국 게임공모전 - 장려상",
      },
      { date: "2010.09.03", content: "제 8회 전국학생게임공모전 - 입선" },
      {
        date: "2017.10.20 - 2017.10.21",
        content: "Seoul 2017 Maker Faire 참가",
      },
      {
        date: "2017.09.29 - 2017.09.30",
        content: "Seoul 2018 Maker Faire 참가",
      },
    ];
    let workArr = [
      { date: "2016.09 - 2020.09", content: "비바엔에스 - 매니저" },
    ];
    let hobbyArr = [
      {
        icon: "icon-health",
        iconArr: ["path1", "path2", "path3"],
        iconName: "건강관리",
      },
      {
        icon: "icon-soccer",
        iconArr: ["path1", "path2", "path3", "path4"],
        iconName: "축구",
      },
      {
        icon: "icon-jewellery",
        iconArr: ["path1", "path2", "path3", "path4"],
        iconName: "금속공예",
      },
    ];

    let profileBlockArr = [
      {
        frontTitle: "E",
        restTitle: "ducation",
        arr: educationArr,
        compStatus: 1,
      },
      {
        frontTitle: "E",
        restTitle: "xperiences",
        arr: experiencesArr,
        compStatus: 1,
      },
      {
        frontTitle: "W",
        restTitle: "ork",
        arr: workArr,
        compStatus: 1,
      },
      {
        frontTitle: "H",
        restTitle: "obby",
        arr: hobbyArr,
        compStatus: 2,
      },
    ];

    let profileBlockComp = profileBlockArr.map((profile) => {

      let arrComp = profile.arr.map((elem) => {
        return (
          <li>
            <span className="date">{elem.date}</span>
            <span className="content">{elem.content}</span>
          </li>
        );
      });

      let hobbyComp = ([] || profile.arr).map((elem) => {
        console.log("elem.iconArr == ", elem.iconArr);
        let icon = elem.iconArr.map((iconElem) => {
          return <span class={iconElem}></span>;
        });
        return (
          <div>
            <div className="icon-font">
              <span class={elem.icon}>{icon}</span>
            </div>
            {elem.name}
          </div>
        );
      });

      return (
        <div className="profile-block">
          <div className="title">
            <font className="front-title">{profile.frontTitle}</font>
            <font className="rest-title">{profile.restTitle}</font>
          </div>
          {profile.compStatus === 1 ? (
            <div>
              <ul>{arrComp}</ul>
            </div>
          ) : (
            <div className="hobby-block">{hobbyComp}</div>
          )}
        </div>
      );
    });

    return (
      <div className="profile-container">
        <div className="left-contents">
          <div className="profile-text">
            Just Do It! 하는 <font>백도형</font>입니다.
          </div>
          <img className="profile-img" src={profileImg} />
          <div className="personality-container">{personalityComp}</div>
        </div>
        <div className="right-contents">
          {profileBlockComp}
          <div className="profile-block">
            <div className="title">
              <font className="front-title">H</font>
              <font className="rest-title">obby</font>
            </div>
            <div className="hobby-block">
              <div>
                <div className="icon-font">
                  <span class="icon-health">
                    <span class="path1"></span>
                    <span class="path2"></span>
                    <span class="path3"></span>
                  </span>
                </div>
                건강관리
              </div>
              <div>
                <div className="icon-font">
                  <span class="icon-soccer">
                    <span class="path1"></span>
                    <span class="path2"></span>
                    <span class="path3"></span>
                    <span class="path4"></span>
                  </span>
                </div>
                축구
              </div>
              <div>
                <div className="icon-font">
                  <span class="icon-jewellery">
                    <span class="path1"></span>
                    <span class="path2"></span>
                    <span class="path3"></span>
                    <span class="path4"></span>
                  </span>
                </div>
                금속공예
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
