import React, { Component } from "react";
import { Scene, SceneItem } from "react-scenejs";

const scene = new Scene(
  {
    ".container": {},
  },
  {
    selector: true,
  }
);

const item = scene.getItem(".container");
class Intro extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    let nextState = {};

    return nextState;
  }

  componentDidMount() {}

  componentWillUnmount() {}

  move = (startTime, endTime, left, top, rotate, scale) => {
    item.set({
      [`${startTime}, ${endTime}`]: Scene.kineticFrame({
        left: `${left}px`,
        top: `${top}px`,
      }).set({
        transform: {
          rotate: `${rotate}deg`,
          scale,
        },
      }),
    });
  };

  render() {
    this.move(0, 0, 90, 115, 0, 5);
    this.move(1, 1, 90, 115, 0, 2);
    this.move(2, 3, 0, 115, 0, 1);
    this.move(4, 4.5, -100, 0, -90, 2);
    this.move(5.5, 6, -52, -18, -90, 1.6);
    this.move(7, 7.5, 30, 45, 0, 2);
    this.move(8.5, 9, 10, 30, 0, 3);
    this.move(10, 10.5, 28, 0, 0, 2.2);
    this.move(11.5, 12, 50, -35, 0, 1.65);
    this.move(13, 13.5, 35, -70, 0, 2);
    this.move(14.5, 18, 0, 0, 0, 1);

    scene.set({
      "[data-typing='i']": Scene.typing({ text: "I ", duration: 1 }),
      "[data-typing='frontend']": {
        1: Scene.typing({ text: "'m Front-End", duration: 1 }),
      },
      "[data-typing='engineer']": {
        1.5: Scene.typing({ text: "Engineer", duration: 1 }),
      },
      "[data-typing='with']": {
        3.3: Scene.typing({ text: "with", duration: 0.5 }),
      },
      "[data-typing='javascript']": {
        4.5: Scene.typing({ text: "JavaScript", duration: 1 }),
      },
      "[data-typing='typescript']": {
        6: Scene.typing({ text: "TypeScript", duration: 1 }),
      },
      "[data-typing='css']": {
        7.5: Scene.typing({ text: "CSS", duration: 0.7 }),
      },
      "[data-typing='nodejs']": {
        9: Scene.typing({ text: "Node.js", duration: 1 }),
      },
      "[data-typing='animation']": {
        10.5: Scene.typing({ text: "Animation", duration: 1 }),
      },
      "[data-typing='scenejs']": {
        12: Scene.typing({ text: "Scene.js", duration: 1 }),
      },
    });

    scene.setPlaySpeed(1);
    scene.setEasing("ease-in-out");
    scene.setIterationCount("infinite");
    scene.play();

    return (
      <div className="intro-container">
        <div class="wrapper">
          <div class="container">
            <a
              href="https://github.com/daybrush"
              target="_blank"
              data-typing="i"
            >
              I
            </a>
            <a
              href="https://github.com/daybrush"
              target="_blank"
              data-typing="frontend"
            >
              'm Front-End
            </a>
            <a
              href="https://github.com/daybrush"
              target="_blank"
              data-typing="engineer"
            >
              Engineer
            </a>
            <p data-typing="with">with</p>
            <p data-typing="javascript">JavaScript</p>
            <p data-typing="typescript">TypeScript</p>
            <p data-typing="css">CSS</p>
            <p data-typing="nodejs">Node.js</p>
            <p data-typing="animation">Animation</p>
            <a
              href="https://github.com/daybrush/scenejs"
              target="_blank"
              data-typing="scenejs"
            >
              Scene.js
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default Intro;
