import React, { Component } from "react";
import profileImg from "../../images/profile_sh.jpg";
import Pagination from "components/Pagination";

class Portfolio extends Component {
  constructor(props) {
    super(props);

    this.state = {
      portfolioArr: [
        {
          idx: 1,
          image: profileImg,
          filterVal: "a",
          title: "test1",
          contents: "test1번의 포트폴리오",
        },
        {
          idx: 2,
          image: profileImg,
          filterVal: "a",
          title: "test2",
          contents: "test2번의 포트폴리오",
        },
        {
          idx: 3,
          image: profileImg,
          filterVal: "b",
          title: "test3",
          contents: "test3번의 포트폴리오",
        },
        {
          idx: 4,
          image: profileImg,
          filterVal: "c",
          title: "test4",
          contents: "test4번의 포트폴리오",
        },
        {
          idx: 5,
          image: profileImg,
          filterVal: "c",
          title: "test5",
          contents: "test5번의 포트폴리오",
        },
        {
          idx: 6,
          image: profileImg,
          filterVal: "c",
          title: "test6",
          contents: "test6번의 포트폴리오",
        },
        {
          idx: 7,
          image: profileImg,
          filterVal: "a",
          title: "test1",
          contents: "test1번의 포트폴리오",
        },
        {
          idx: 8,
          image: profileImg,
          filterVal: "a",
          title: "test2",
          contents: "test2번의 포트폴리오",
        },
        {
          idx: 9,
          image: profileImg,
          filterVal: "b",
          title: "test3",
          contents: "test3번의 포트폴리오",
        },
        {
          idx: 10,
          image: profileImg,
          filterVal: "c",
          title: "test4",
          contents: "test4번의 포트폴리오",
        },
        {
          idx: 11,
          image: profileImg,
          filterVal: "c",
          title: "test5",
          contents: "test5번의 포트폴리오",
        },
        {
          idx: 12,
          image: profileImg,
          filterVal: "c",
          title: "test6",
          contents: "test6번의 포트폴리오",
        },
      ], //포트폴리오 전체 json
      showPortfolioArr: [], //한페이지에 보여줘야 할 json
      fliterPortfolioArr: [], //포트폴리오 필터 state
      totalCount: 6, //이벤트 영상 전체 갯수
      activePage: 1, //active 된 페이지
      rowCount: 6, //화면에 보여줄 갯수
    };
  }

  componentDidMount() {
    const { portfolioArr } = this.state;
    let _portfolioArr = portfolioArr.slice(0, 2);

    console.log(_portfolioArr);

    this.setState({
      // fliterPortfolioArr: portfolioArr
      showPortfolioArr: _portfolioArr,
    });
  }

  //여기서 페이지 눌렀을때 json 값 변경
  componentDidUpdate(prevProps, prevState) {
    const { portfolioArr, showPortfolioArr, fliterPortfolioArr } = this.state;
    const { rowCount, activePage } = this.state;
    let offset = rowCount * activePage - rowCount;

    console.log("offset == ", offset);

    if (prevState.activePage !== activePage) {
      let _portfolioArr = portfolioArr.slice(offset, offset + rowCount);
      this.setState({ showPortfolioArr: _portfolioArr });
      // let totalCount = this.getEventVideoTotal(
      //   cctvList[selectIdx].id,
      //   startDate,
      //   endDate,
      //   rowCount,
      //   offset
      // );
      // this.setState({
      //   totalCount,
      // });
    }
  }

  handleOnClick = (num) => {
    const { portfolioArr } = this.state;
    let _portfolioArr = portfolioArr;

    if (num === 0) {
      _portfolioArr = _portfolioArr;
    } else if (num === 1) {
      _portfolioArr = _portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "a"
      );
    } else if (num === 2) {
      _portfolioArr = _portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "b"
      );
    } else if (num === 3) {
      _portfolioArr = _portfolioArr.filter(
        (portfolio) => portfolio.filterVal === "c"
      );
    }

    this.setState({ fliterPortfolioArr: _portfolioArr });
  };

  //페이지 변경될때 호출되는 메소드
  handleOnPageChange = (pageNumber) => {
    this.setState({
      activePage: pageNumber,
    });
  };

  render() {
    // const { fliterPortfolioArr } = this.state;
    const { showPortfolioArr } = this.state;
    const { totalCount, rowCount, activePage } = this.state;

    let filterList = ["전체", "aaaaaa", "bbbbbb", "cccccc"];
    let filterComp = filterList.map((filter, idx) => {
      return (
        <div
          onClick={() => {
            this.handleOnClick(idx);
          }}
        >
          {filter}
        </div>
      );
    });

    // let portfolioComp = (fliterPortfolioArr || []).map((portfolio, idx) => {
    //   return (
    //     <div className="screen">
    //       {/* {portfolio.title} / {idx} */}
    //       <span />
    //       <span />
    //       <span />
    //       <span />
    //       <img src={profileImg} />
    //     </div>
    //   );
    // });

    let portfolioComp = (showPortfolioArr || []).map((portfolio, idx) => {
      return (
        <div className="screen">
          <span />
          <span />
          <span />
          <span />
          <div>
            {portfolio.title}
          </div>
          <img src={profileImg} />
        </div>
      );
    });

    return (
      <div className="portfolio-container">
        <div className="filter-container">{filterComp}</div>
        <Pagination
          onPageChange={this.handleOnPageChange}
          total={totalCount}
          rowCount={rowCount}
          currentPage={activePage}
        />
        <div className="portfolio-list">{portfolioComp}</div>
      </div>
    );
  }
}

export default Portfolio;
